import * as React from "react";
import Stack from "@mui/material/Stack";
import Stepper from "@mui/material/Stepper";
import Step from "@mui/material/Step";
import StepLabel from "@mui/material/StepLabel";
import { ColorlibConnector, ColorlibStepIcon } from "./style/Icon";

const STEPS = ["request push", "std-notification", "ccs-notification"];

function getNormalIcon(data) {
    // if (data.status === "SUCCESS") return;
    // console.log("getNormalIcon:", data);
    return ColorlibStepIcon; // data 기준으로 아이콘 만들고 싶은데..
}

function getStepIcons(data) {
    return STEPS.map((label, i) => {
        const labelProps = {};
        if (data.steps[i] === undefined) {
            labelProps.active = false;
        } else if (data.steps[i].status === "FAILURE") {
            labelProps.error = true;
        }

        return (
            <Step key={label}>
                <StepLabel {...labelProps} StepIconComponent={ColorlibStepIcon} StepIconProps={{ step: data.steps[i] }}>
                    {label}
                </StepLabel>
            </Step>
        );
    });
}

//응답값이 넘어온다.
//응답을 분석해서 아이콘을 생성한다.
export default function TransactionStepper(props) {
    const { data } = props;
    return (
        <Stack sx={{ width: "100%" }} spacing={4}>
            <Stepper alternativeLabel activeStep={1} connector={<ColorlibConnector />}>
                {getStepIcons(data)}
            </Stepper>
        </Stack>
    );
}
