import "./App.css";
import axios from "axios";
import React, { useState } from "react";
import { SearchForm, TransactionResult, Spinner } from "./Components";

function App() {
    const [loading, setLoading] = useState(false);
    const [done, setDone] = useState(true);
    const [data, setData] = useState(null); // API로부터 받아온 데이터
    const [error, setError] = useState(null); // 오류 상태 관리

    const handleSubmit = async tid => {
        setLoading(true);
        setError(null);
        try {
            const response = await axios.get(`http://localhost:8080?tid=${tid}`);
            setData(response.data); // 데이터 설정
        } catch (err) {
            setError("Failed to fetch data. Please try again.");
        } finally {
            setLoading(false); // 로딩 완료 상태로 전환
        }
    };
    return (
        <>
            <SearchForm onHandleSubmit={handleSubmit} />
            {loading && <Spinner />}
            {done && <TransactionResult />}
        </>
    );
}

export default App;
