import * as React from "react";
import Stack from "@mui/material/Stack";
import Stepper from "@mui/material/Stepper";
import Step from "@mui/material/Step";
import StepLabel from "@mui/material/StepLabel";
import { ColorlibConnector, ColorlibStepIcon } from "./style/Icon";

const STEPS = ["request push", "std-notification", "ccs-notification"];
const data = {
    activeStep: 1,
    data: [
        {
            phase: 0,
            transactionLog: "PHASE 0 Transaction Log..",
        },
        {
            phase: 1,
            transactionLog: "PHASE 0 Transaction Log..",
        },
    ],
};

function getStepIcons(data) {
    return STEPS.map((label, i) => {
        const labelProps = {};
        if (i <= data.activeStep) {
            labelProps.error = true;
        }

        return (
            <Step key={label}>
                <StepLabel {...labelProps} StepIconComponent={ColorlibStepIcon}>
                    {label}
                </StepLabel>
            </Step>
        );
    });
}

//응답값이 넘어온다.
//응답을 분석해서 아이콘을 생성한다.
export default function TransactionStepper() {
    return (
        <Stack sx={{ width: "100%" }} spacing={4}>
            <Stepper alternativeLabel activeStep={1} connector={<ColorlibConnector />}>
                {getStepIcons(data)}
            </Stepper>
        </Stack>
    );
}
