import "./App.css";
import React, { useState } from "react";
import { SearchForm, TransactionResult, Spinner } from "./Components";

function App() {
    const [loading, setLoading] = useState(false);
    const [done, setDone] = useState(true);

    const handleSubmit = data => {
        setLoading(data);
        setDone(false);

        //fetch somewhere..
        setTimeout(() => {
            setLoading(false);
            setDone(true);
        }, 2000);
    };

    const transactionResultDataSuccess = {
        status: "success",
        failedAt: -1,
        transactionLog: "success transaction log.....",
    };
    const transactionResultDataFailed = {
        status: "failed",
        failedAt: 0,
        transactionLog: "step 0 failed transaction log.....",
    };

    return (
        <>
            <SearchForm onHandleSubmit={handleSubmit} />
            {loading && <Spinner />}
            {done && <TransactionResult />}
        </>
    );
}

export default App;
