import React from "react";
import Accordion from "@mui/material/Accordion";
import AccordionSummary from "@mui/material/AccordionSummary";
import AccordionDetails from "@mui/material/AccordionDetails";
import Typography from "@mui/material/Typography";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import Button from "@mui/material/Button";
import ContentCopyIcon from "@mui/icons-material/ContentCopy";

export default function TransactionLog({ data }) {
    const handleCopy = text => {
        navigator.clipboard
            .writeText(text)
            .then(() => {
                alert("Transaction log copied to clipboard!");
            })
            .catch(err => {
                console.error("Failed to copy: ", err);
            });
    };
    return (
        <div>
            {data.steps.map((step, index) => (
                <Accordion
                    key={index}
                    disabled={step.status === "DISABLED"}
                    defaultExpanded={step.status !== "DISABLED"}
                    sx={{
                        opacity: step.status === "DISABLED" ? 0.5 : 1,
                        pointerEvents: step.status === "DISABLED" ? "none" : "auto",
                    }}
                >
                    <AccordionSummary expandIcon={<ExpandMoreIcon />} aria-controls={`panel${index}-content`} id={`panel${index}-header`}>
                        <Typography>Phase {step.phase}</Typography>
                    </AccordionSummary>
                    <AccordionDetails>
                        <Typography>{step.transactionLog}</Typography>
                        <Button variant="outlined" startIcon={<ContentCopyIcon />} onClick={() => handleCopy(step.transactionLog)} sx={{ mt: 1 }}>
                            Copy
                        </Button>
                    </AccordionDetails>
                </Accordion>
            ))}
        </div>
    );
}
